package trycatch;

public class switchst {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int day=4;
		switch(day)
		{
		case 1:	
			 System.out.println("MONDAY");
			break;
			case 2:
				 System.out.println("TUE");
			break;
			case 3:
				 System.out.println("WED");
					break;
			case 4:
				 System.out.println("thru");
					break;
				
		}
	}

}
